```typescript
// app/api/chat/completions/route.ts
import { createServerClient, getApiKeyFromCookie } from '@ainative/next-sdk/server';
import { NextRequest, NextResponse } from 'next/server';
import { cookies } from 'next/headers';

export async function POST(request: NextRequest) {
  try {
    // Get API key from cookie or header
    const cookieStore = cookies();
    const apiKey = await getApiKeyFromCookie(cookieStore);

    if (!apiKey) {
      return NextResponse.json(
        { error: 'Unauthorized', message: 'No API key found' },
        { status: 401 }
      );
    }

    // Parse request body
    const body = await request.json();
    const { messages, model = 'llama-3.3-70b-instruct', temperature = 0.7, max_tokens = 1000 } = body;

    // Validate messages
    if (!messages || !Array.isArray(messages) || messages.length === 0) {
      return NextResponse.json(
        { error: 'Bad Request', message: 'messages array is required' },
        { status: 400 }
      );
    }

    // Create server client
    const client = createServerClient({ apiKey });

    // Make chat completion request
    const response = await client.chat.completions.create({
      messages,
      preferred_model: model,
      temperature,
      max_tokens
    });

    // Return successful response
    return NextResponse.json({
      success: true,
      data: response,
      timestamp: new Date().toISOString()
    });

  } catch (error: any) {
    console.error('Chat completion error:', error);

    // Handle specific error cases
    if (error.message?.includes('402')) {
      return NextResponse.json(
        { error: 'Payment Required', message: 'Insufficient credits' },
        { status: 402 }
      );
    }

    if (error.message?.includes('429')) {
      return NextResponse.json(
        { error: 'Too Many Requests', message: 'Rate limit exceeded' },
        { status: 429 }
      );
    }

    return NextResponse.json(
      { error: 'Internal Server Error', message: error.message || 'Something went wrong' },
      { status: 500 }
    );
  }
}

// Optional: GET endpoint for checking status
export async function GET() {
  return NextResponse.json({
    service: 'AINative Chat API',
    status: 'operational',
    version: '1.0.0'
  });
}
